from datetime import datetime

#----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------#
def add_years(dt, years):                                                                                                                                                                                                          #
  try:                                                                                                                                                                                                                             #
      result = datetime(dt.year + years, dt.month, dt.day)                                                                                                                                                                         #
  except ValueError:                                                                                                                                                                                                               #
      result = datetime(dt.year + years, dt.month, dt.day - 1)                                                                                                                                                                     #
  return result                                                                                                                                                                                                                    #
                                                                                                                                                                                                                                   #
#----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------#
